mapper.select:
SELECT COUNT(*) FROM POST WHERE P_DELETE_DATE IS NULL
AND P_SUB_CATEGORY = #{category}
AND { p_title LIKE '%' || #{keyword} || '%' OR p_content LIKE '%' || #{keyword} || '%' OR p_review LIKE '%' || #{keyword} || '%' OR p_vote_title LIKE '%' || #{keyword} || '%' OR p_vote_1ST LIKE '%' || #{keyword} || '%' OR p_vote_2ND LIKE '%' || #{keyword} || '%' }

SELECT * FROM POST WHERE P_DELETE_DATE IS NULL
AND P_SUB_CATEGORY = #{category}
AND { p_title LIKE '%' || #{keyword} || '%' OR p_content LIKE '%' || #{keyword} || '%' OR p_review LIKE '%' || #{keyword} || '%' OR p_vote_title LIKE '%' || #{keyword} || '%' OR p_vote_1ST LIKE '%' || #{keyword} || '%' OR p_vote_2ND LIKE '%' || #{keyword} || '%' }
ORDER BY p_create_date DESC

mapper.update:
UPDATE POST
	SET P_VIEWS = #{views}
	WHERE P_IDX = #{idx}

UPDATE POST
	SET P_STATUS = #{status}
	WHERE P_IDX = #{idx}

mapper.select:
SELECT COUNT(*) FROM "COMMENT"
WHERE C_DELETE_DATE IS NULL 
AND P_IDX = #{idx}

SELECT COUNT(*) FROM VOTE
WHERE P_IDX = #{postIdx}

AND V_SELECT = #{seleted}

SELECT COUNT(*) FROM VOTE
WHERE P_IDX = #{postIdx}
AND M_IDX = #{memIdx}

SELECT * FROM VOTE
	WHERE P_IDX = #{postIdx}
	AND M_IDX = #{memIdx}

mapper.delete:
DELETE FROM VOTE
WHERE P_IDX = #{postIdx}
AND M_IDX = #{memIdx}
